    static short _p_ = 1;
    if ( _p_ )
        writeLog(LOG_INF, "FnCall=> Read_RdcSms_Cdr");
    _p_ = 0;
#endif
    memset(pbuf_rdcsms, 0x00, sizeof (pbuf_rdcsms));
    return (Read_Delimiter_Cdr('|', NOF_FLD_RDCSMS, pbuf_rdcsms));
